mapper.select:
SELECT fh.id historyId, fh.instance_id instanceId, fh.step_id stepId, fh.audit_user_id auditUserId, fh.audit_info auditInfo, fh.allot_user_id allotUserId, fh.audit_result auditResult, fh.form_type formType, fh.form_id formId, fh.form_no formNo, fh.create_time createTime, s.`name` stepName, s.description description, su.real_name userName FROM flow_history fh LEFT JOIN step s ON s.id = fh.step_id LEFT JOIN sys_user su ON su.id = fh.audit_user_id
and fh.instance_id = #{instanceId}
and fh.step_id =#{stepId}
order by fh.create_time asc
