mapper.select:
select sum(inspect.total_num) total_num,inspect.product_no,inspect.product_model,sum(inspect.total_num*associate.price) total_price from (select sum(pro_inspect_record.`qualified_number`) total_num,pro_inspect_record.product_no,pro_inspect_record.product_model,pro_inspect_record.order_no from pro_inspect_record where product_no !=''
and FROM_UNIXTIME(UNIX_TIMESTAMP(create_time),"%Y") = #{year}
group by pro_inspect_record.product_no,pro_inspect_record.order_no) as inspect, (select order_product_associate.`product_no`,order_product_associate.`price`,order_product_associate.`order_no` from order_product_associate where 1=1
and FROM_UNIXTIME(UNIX_TIMESTAMP(create_time),"%Y") = #{year}
group by order_product_associate.`product_no`,order_product_associate.`order_no` order by order_no) as associate where inspect.product_no = associate.product_no and inspect.order_no = associate.order_no

select sum(inspect.total_num) total_num,inspect.product_no,inspect.product_model,sum(inspect.total_num*associate.price) total_price from (select sum(pro_inspect_record.`qualified_number`) total_num,pro_inspect_record.product_no,pro_inspect_record.product_model,pro_inspect_record.order_no from pro_inspect_record where product_no !=''
and FROM_UNIXTIME(UNIX_TIMESTAMP(create_time),"%Y") = #{year}
group by pro_inspect_record.product_no,pro_inspect_record.order_no) as inspect, (select order_product_associate.`product_no`,order_product_associate.`price`,order_product_associate.`order_no` from order_product_associate group by order_product_associate.`product_no`,order_product_associate.`order_no` order by order_no) as associate where inspect.product_no = associate.product_no and inspect.order_no = associate.order_no group by inspect.product_model

select sum(`total_amount`) total_price,invoice_company as demand_name,FROM_UNIXTIME(UNIX_TIMESTAMP(create_time),"%Y") as year from invoice where type=1 and status in(2)
and FROM_UNIXTIME(UNIX_TIMESTAMP(create_time),"%Y") = #{year}
group by invoice_company

select ifnull(sum(`total_amount`),0) total_price,invoice_company as demand_name,FROM_UNIXTIME(UNIX_TIMESTAMP(create_time),"%Y") year from invoice where type=1 and status in(1,2)
and FROM_UNIXTIME(UNIX_TIMESTAMP(create_time),"%Y") = #{year}
group by invoice_company
